import React from 'react'

const AllProducts = () => {
    const [products, setProducts] = useState([]);

    useEffect(() => {
      axios.get("/api/products").then((response) => {
        setProducts(response.data);
      });
    }, []);

    return (
      <Container>
        <h1>All Products</h1>
        <table>
          <thead>
            <tr>
              <th>Name</th>
              <th>Description</th>
              <th>Price</th>
            </tr>
          </thead>
          <tbody>
            {products.map((product) => (
              <tr key={product.id}>
                <td>{product.name}</td>
                <td>{product.description}</td>
                <td>{product.price}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </Container>
    );
}

export default AllProducts
